class Solution {
    class Pair {
        public int row;
        public int col;
        public int value;
        public Pair(int row, int col, int value) {
            this.row = row;
            this.col = col;
            this.value = value;
        }
    }
    public long maximumValueSum(int[][] board) {
        int m = board.length;
        int n = board[0].length;
        List<Pair> ls = new ArrayList<>();
        for(int i=0;i<m;i++) {
            for(int j=0;j<n;j++) {
                ls.add(new Pair(i, j, board[i][j]));
            }
        }
        Collections.sort(ls, (a,b) -> b.value - a.value);
        long max = Long.MIN_VALUE;
        for(int i=0;i< m*n-2; i++) {
            boolean ans = false;
            for(int j=i+1; j < m*n-1;j++) {
                for(int k=j+1; k < m*n; k++) {
                    Pair A = ls.get(i);
                    Pair B = ls.get(j);
                    Pair C = ls.get(k);
                    if(A.row != B.row && B.row != C.row && C.row != A.row &&  A.col != B.col && B.col != C.col && A.col!=C.col) {
                        max = Math.max(max, (long)A.value + (long)B.value + (long)C.value);
                        break;
                    }
                    if(max > (long)A.value + (long)B.value + (long)C.value) {
                        break;
                    }
                }
            }
        }
        return max;
    }
}
