typedef pair<int, int> pii;

class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& v) {
        int n = (int) v.size();
        int m = (int) v[0].size();

        vector<vector<array<int, 2>>> r(n, vector<array<int, 2>>(3));

        for (int i = 0; i < n; i++) {
            vector<array<int, 2>> nv(m);
            for (int j = 0; j < m; j++) {
                nv[j] = {v[i][j], j};
            }
            sort(nv.rbegin(), nv.rend());
            for (int j = 0; j < 3; j++) {
                r[i][j] = nv[j];
            }
        }

        long long ans = -1e13;

        for (int r1 = 0; r1 < n; r1++) {
            for (int r2 = r1 + 1; r2 < n; r2++) {
                for (int x = 0; x < 3; x++) {
                    for (int y = 0; y < 3; y++) {

                        auto [p1, q1] = r[r1][x];
                        auto [p2, q2] = r[r2][y];

                        if (q1 == q2) {
                            continue;
                        }

                        for (int r3 = r2 + 1; r3 < n; r3++) {
                            for (int z = 0; z < 3; z++) {
                                auto [val, col] = r[r3][z];
                                if (col != q1 and col != q2) {
                                    ans = max(ans, 0LL + p1 + p2 + val);
                                    break;
                                }
                            }
                        }
                    }
                }
            }
        }

        return ans;
    }
};
