class Solution:
    def maximumValueSum(self, board: List[List[int]]) -> int:
        nums = []
        for i, row in enumerate(board):
            for j, col in enumerate(row):
                nums.append([col, i, j])
        nums = sorted(nums, key=lambda x: x[0], reverse=True)
        print(nums)
        ans = -inf
        for i in range(len(nums) - 2):
            local_sum = nums[i][0] + nums[i + 1][0] + nums[i + 2][0]
            if local_sum <= ans:
                continue
            for j in range(i + 1, len(nums) - 1):
                local_sum = nums[i][0] + nums[j][0] + nums[j+1][0]
                if local_sum <= ans:
                    continue
                for k in range(j + 1, len(nums)):
                    rook_1, rook_2, rook_3 = nums[i], nums[j], nums[k]
                    local_sum = nums[i][0] + nums[j][0] + nums[k][0]
                    if local_sum <= ans:
                        continue
                    if rook_1[1] != rook_2[1] and rook_1[1] != rook_3[1] and rook_3[1] != rook_2[1] and rook_1[2] != rook_2[2] and rook_1[2] != rook_3[2] and rook_3[2] != rook_2[2]:
                        ans = max(ans, local_sum)
        return ans
