#define ll long long
vector<array<int,3>> pos;
ll INF=1e18;
int n,m;
ll solve(int r,int c){
    array<int,3> mx={-1,-1,-1},smx;
    for(int i=0;i<n+m+2;i++){
        if(!(pos[i][1]==r or pos[i][2]==c)){
            if(mx[0]==-1) mx={pos[i][1],pos[i][2],pos[i][0]};
            else{
                smx={pos[i][1],pos[i][2],pos[i][0]};
                break;
            }
        }
    }
    ll ans=-INF;
    for(int i=0;i<min(n*m,2*(n+m+2));i++){
        if(!(pos[i][1]==r or pos[i][2]==c) and !(pos[i][1]==mx[0] or pos[i][2]==mx[1])) ans=max(ans,(ll)mx[2]+pos[i][0]);
        if(!(pos[i][1]==r or pos[i][2]==c) and !(pos[i][1]==smx[0] or pos[i][2]==smx[1])) ans=max(ans,(ll)smx[2]+pos[i][0]);
    }
    return ans;
}

class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        n=board.size(),m=board[0].size();
        ll ans=-INF;
        pos.clear();
        for(int i=0;i<n;i++) for(int j=0;j<m;j++) pos.push_back({board[i][j],i,j});
        sort(pos.begin(),pos.end());
        reverse(pos.begin(),pos.end());
        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){
                ans=max(ans,(ll)board[i][j]+solve(i,j));
            }
        }
        return ans;
    }
};
