class Solution {
    typedef long long ll;
public:
    long long maximumValueSum(vector<vector<int>>& v) {

        ll a = v.size();
        ll b = v[0].size();
        ll ans = -1e10;

        multimap <ll, pair<int, int>, greater<ll>> m, x;
        multimap <ll, vector <int>, greater<ll>> y;

        for(int i = 0; i < a; i++){
            for(int j = 0; j < b; j++){
                m.insert({v[i][j], {i, j}});
            }
        }

        for(auto & e : m){

            ll p1 = e.first, q1 = e.second.first, r1 = e.second.second;
            vector <int> t(4);
            t[0] = q1;
            t[2] = r1;

            for(auto & itr : y){

                if(itr.first + p1 <= ans)
                    break;

                if(q1 != itr.second[0] && q1 != itr.second[1]){
                    if(r1 != itr.second[2] && r1 != itr.second[3]){
                        ans = max(ans, itr.first + p1);
                        break;
                    }
                }
            }

            int cnt = 0;
            for(auto & itr : x){
                if(itr.second.first != q1 && itr.second.second != r1){
                    t[1] = itr.second.first;
                    t[3] = itr.second.second;

                    y.insert({p1 + itr.first, t});
                    cnt++;
                }

                if(cnt == 3)
                    break;
            }

            x.insert({p1, {q1, r1}});
        }
        return ans;
    }
};
