class Solution {
public:
    using ll = long long;
    long long maximumValueSum(vector<vector<int>>& b) {
        int n = b.size();
        int m = b[0].size();
        ll ans = -1e18;
        ll inf = -1e10;
        vector<pair<int, int>> a(n * m);
        for (int i = 0; i < n * m; i++) {
            a[i] = {i / m, i % m};
        }
        sort(a.begin(), a.end(), [&](auto& x1, auto& x2){
            return b[x1.first][x1.second] > b[x2.first][x2.second];
        });

        int p = min((m + n) * 3, n * m);

        for (int i = 0; i < p; i++) {
            for (int j = i + 1; j < p; j++) {
                for (int k = j + 1; k < p; k++) {
                    int x1 = a[i].first, x2 = a[j].first, x3 = a[k].first;
                    int y1 = a[i].second, y2 = a[j].second, y3 = a[k].second;
                    if (x1 == x2 || x3 == x1 || x2 == x3) continue;
                    if (y1 == y2 || y3 == y1 || y2 == y3) continue;
                    ll res =  b[x1][y1];
                    res += b[x2][y2];
                    res += b[x3][y3];
                    ans = max(ans, res);
                    break;
                }
            }
        }
        return ans;
    }
};
