class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        int n = board.size();
        int m = board[0].size();
        priority_queue<pair<int,pair<int,int>>,vector<pair<int,pair<int,int>>>,greater<pair<int,pair<int,int>>>>pq;
        for(int i = 0;i<n;i++)
        {
            for(int j=0;j<m;j++)
            {
                pq.push({board[i][j],{i,j}});
                if(pq.size()>500)
                {
                    pq.pop();
                }
            }
        }
        vector<pair<long,pair<long,long>>>v;
        while(!pq.empty())
        {
            auto a = pq.top();
            pq.pop();
            v.push_back(a);
        }
        long long ans = -1e12;
        for(int i = 0;i<(v.size()-2);i++)
        {
            for(int j = i+1;j<(v.size()-1);j++)
            {
                for(int k = j+1;k<(v.size());k++)
                {
                    int l1 = v[i].second.first, r1 = v[i].second.second;
                    int l2 =v[j].second.first, r2 = v[j].second.second;
                    int l3 = v[k].second.first, r3 = v[k].second.second;
                    if(l1!=l2&&l2!=l3&&l1!=l3&&r1!=r2&&r2!=r3&&r1!=r3)
                    {
                        ans = max(ans,(long long)(v[i].first+v[j].first+v[k].first));
                    }
                }
            }
        }
        return ans;
    }
};
