class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& a) {
        int n = a.size(), m = a[0].size();
        using ll = long long;
        vector<pair<ll, pair<int, int>>> v;
        for (int i = 0; i < n; ++i) {
            vector<pair<int, pair<int, int>>> r;
            for (int j = 0; j < m; ++j) {
                r.push_back({a[i][j], {i, j}});
            }

            // only best 3 cols
            sort(r.rbegin(), r.rend());
            for (int j = 0; j < 3; ++j) v.push_back(r[j]);
        }
        sort(v.rbegin(), v.rend());
        int V = v.size();

        ll ans = -3e9;
        for (int i = 0; i < V; ++i) {
            for (int j = i+1; j < V; ++j) {
                for (int k = j+1; k < V; ++k) {
                    if (v[i].second.first != v[j].second.first && v[i].second.first != v[k].second.first && v[k].second.first != v[j].second.first) {
                        if (v[i].second.second != v[j].second.second && v[i].second.second != v[k].second.second && v[k].second.second != v[j].second.second) {
                            long long t = v[i].first + v[j].first + v[k].first;
                            ans = max(ans, t);
                        }
                    }
                }
            }
        }
        return ans;
    }
};
