class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        ios_base::sync_with_stdio(false);
        cin.tie(NULL);

        vector<pair<long long, pair<int, int>>> arr;
        long long n = board.size(), m = board[0].size();

        for(long long i = 0; i < n; i++)
            for(long long j = 0; j < m; j++)  
                arr.push_back({(long long)board[i][j], {i, j}});

        sort(arr.begin(), arr.end(), greater<pair<long long, pair<int, int>>>());

        vector<bool> row(n, false), col(m, false);
        long long mx = LLONG_MIN;

        long long limit = min((long long)arr.size(), 100LL);

        for(long long i = 0; i < limit; i++) {
            long long r1 = arr[i].second.first;
            long long c1 = arr[i].second.second;

            if (row[r1] || col[c1]) continue;
            row[r1] = col[c1] = true;

            for(long long j = i + 1; j < limit; j++) {
                long long r2 = arr[j].second.first;
                long long c2 = arr[j].second.second;

                if (row[r2] || col[c2]) continue;
                row[r2] = col[c2] = true;

                for(long long k = j + 1; k < limit; k++) {
                    long long r3 = arr[k].second.first;
                    long long c3 = arr[k].second.second;

                    if (row[r3] || col[c3]) continue;

                    long long currentSum = arr[i].first + arr[j].first + arr[k].first;
                    mx = max(mx, currentSum);

                    break;
                }

                row[r2] = col[c2] = false;
            }

            row[r1] = col[c1] = false;
        }

        return mx;
    }
};
