class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        int n = board.size();
        int m = board[0].size();
        vector<vector<int>> max(n, vector<int>(3, INT_MIN));
        vector<vector<int>> maxInd(n, vector<int>(3, INT_MIN));

        for(int i = 0; i<n; i++){
            for(int j = 0; j<m; j++){
                if(board[i][j] > max[i][0]){
                    max[i][2] = max[i][1];
                    max[i][1] = max[i][0];
                    max[i][0] = board[i][j];
                    maxInd[i][2] = maxInd[i][1];
                    maxInd[i][1] = maxInd[i][0];
                    maxInd[i][0] = j;
                }
                else if(board[i][j] > max[i][1]){
                    max[i][2] = max[i][1];
                    max[i][1] = board[i][j];
                    maxInd[i][2] = maxInd[i][1];
                    maxInd[i][1] = j;
                }
                else if(board[i][j] > max[i][2]){
                    max[i][2] = board[i][j];
                    maxInd[i][2] = j;
                }
            }
        }
        long t1, t2, t3;
        int tj1, tj2, tj3;
         int anstj1, anstj2, anstj3;
        int ansti1, ansti2, ansti3;
        long ans = LONG_MIN;
        for(int i1 =0; i1<n; i1++){
            t1 = max[i1][0];
            tj1 = maxInd[i1][0];
            for(int i2=0; i2<n; i2++){
                if(i1 == i2) continue;
                if(tj1 == maxInd[i2][0]){
                    t2 = max[i2][1];
                    tj2 = maxInd[i2][1];
                }
                else{
                    t2 = max[i2][0];
                    tj2 = maxInd[i2][0];
                }
                for(int i3=0; i3<n; i3++){
                    if(i3 == i1 || i3 == i2) continue;
                    if((tj1 == maxInd[i3][0] && tj2 == maxInd[i3][1])||((tj2 == maxInd[i3][0] && tj1 == maxInd[i3][1]))){
                        t3 = max[i3][2];
                        tj3 = maxInd[i3][2];
                    }
                    else if((tj1 == maxInd[i3][0] )||((tj2 == maxInd[i3][0] ))){
                        t3 = max[i3][1];
                        tj3 = maxInd[i3][1];
                    }
                    else{
                        t3 = max[i3][0];
                        tj3 = maxInd[i3][0];
                    }
                   if(t1+t2+t3 > ans){
                       ans = t1+t2+t3;
                            anstj1 = tj1;
                            anstj2 = tj2;
                            anstj3 = tj3;
                            ansti1 = i1;
                            ansti2 = i2;
                            ansti3 = i3;
                   }
                }
            }
        }
        // cout << anstj1 << " " <<  anstj2 << " " << anstj3 <<" "<< ansti1 << " " << ansti2 << " " << ansti3; 
        return ans;
    }
};
