limit = 10**11
debug = 0
class Solution:
    def maximumValueSum(self, board: List[List[int]]) -> int:
        m,n = len(board),len(board[0])
        out = -limit
        one = [-limit] * n
        two = [-limit] * (n**2)
        for ri,row in enumerate(board):
            if ri>1:
                h = []
                for j,v in enumerate(row):
                    heappush(h,[v,j])
                    if len(h) == 4:
                        heappop(h)
                h.sort()
                for key in range(n**2):
                    x,y = key//n,key%n
                    v = two[key]
                    for j in range(2,-1,-1):
                        if h[j][1] != x and h[j][1]!=y:
                            out = max(out,v+h[j][0])
                            break
            if ri:
                for i in range(n):
                    for j in range(n):
                        if i == j:
                            continue
                        key = min(i,j)*n+max(i,j)
                        two[key] = max(two[key],one[i]+row[j])
            for i in range(n):
                one[i] = max(one[i],row[i])
            if debug:
                print("==",row)
                print(one)
                print(two)
                print(out)
        return out
