class Solution:
    def maximumValueSum(self, board: List[List[int]]) -> int:
        m, n = len(board), len(board[0])
        pairs = []
        for i in range(m):
            for j in range(n):
                pairs.append((board[i][j], i, j))
        pairs.sort(key=lambda x:-x[0])
        ans = -inf
        for i in range(len(pairs)):
            x, r, c = pairs[i]
            j = 0
            while pairs[j][1] == r or pairs[j][2] == c:
                j += 1
            mark = -1
            flag = True
            for k in range(j, len(pairs)):
                if k == mark:
                    break
                if pairs[k][1] != r and pairs[k][2] != c:
                    y, r1, c1 = pairs[k]
                    p = 0
                    while pairs[p][1] == r or pairs[p][1] == r1 or pairs[p][2] == c or pairs[p][2] == c1:
                        p += 1
                    ans = max(ans, x + y + pairs[p][0])
                    if flag:
                        mark = p
                        flag = False
        return ans
