class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& v1) {
        int n=(int)v1.size();
        int m=(int)v1[0].size();
        vector<vector<long long>> v(n,vector<long long>(m));
        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++) v[i][j]=v1[i][j];
        }
        vector<vector<pair<long long,long long>>> s(n);
        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){
                s[i].push_back({v[i][j],j});
            }
            sort(s[i].rbegin(),s[i].rend());
        }
        long long ans=-1e18;
        for(int i=0;i<n;i++){
            for(int j=i+1;j<n;j++){
                for(int k=j+1;k<n;k++){
                    vector<pair<long long ,long long>> a,b,c;
                    for(int i1=0;i1<3;i1++){
                        a.push_back(s[i][i1]);
                        b.push_back(s[j][i1]);
                        c.push_back(s[k][i1]);
                    }
                    for(int i1=0;i1<3;i1++){
                        for(int j1=0;j1<3;j1++){
                            for(int k1=0;k1<3;k1++){
                                if(a[i1].second!=b[j1].second && b[j1].second!=c[k1].second && a[i1].second!=c[k1].second) ans=max(ans,a[i1].first+b[j1].first+c[k1].first);
                            }
                        }
                    }
                }
            }
        }
        return ans;
    }
};
