#define forz(i,a,b) for(ll i = a;i<b;i++)
typedef long long ll;

class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& grid) {
        int n = grid.size();
        int m = grid[0].size();
        vector<pair<ll,ll>> rowmax1(n),rowmax2(n),rowmax3(n);

        forz(i,0,n){
            vector<pair<ll,ll>> temp;
            forz(j,0,m){
                temp.push_back({grid[i][j],j});
            }
            sort(temp.rbegin(),temp.rend());
            rowmax1[i] = temp[0];
            rowmax2[i] = temp[1];
            rowmax3[i] = temp[2];
        }

        long long ans = -1e10;

        forz(i,0,n){
            forz(j,0,m){
                long long tempAns = -1e10;
                forz(row1,0,n){
                    if (row1 == i){
                        continue;
                    }
                    int col = -1;
                    long long temp = -1;

                    if (rowmax1[row1].second != j){
                        col = rowmax1[row1].second;
                        temp = rowmax1[row1].first;
                    }
                    else{
                        col = rowmax2[row1].second;
                        temp = rowmax2[row1].first;
                    }

                    forz(row2,0,n){
                        if ((row2 == i) || (row2 == row1)){
                            continue;
                        }

                        if ((rowmax1[row2].second != j) && (rowmax1[row2].second != col)){
                            tempAns = max(tempAns, temp + rowmax1[row2].first);
                        } 
                        else if ((rowmax2[row2].second != j) && (rowmax2[row2].second != col)){
                            tempAns = max(tempAns, temp + rowmax2[row2].first);
                        }  
                        else if ((rowmax3[row2].second != j) && (rowmax3[row2].second != col)){
                            tempAns = max(tempAns, temp + rowmax3[row2].first);
                        }
                    }
                }

                ans = max(ans,grid[i][j] + tempAns);
            }
        }

        return ans;

    }
};
