#include <bits/stdc++.h>
using namespace std; 
#define all(c) c.begin(),c.end()
#define sz(x) int(x.size())
#define ar array
typedef long long ll; 
typedef vector<int> vi;
const ll mod=1000000007;
mt19937_64 rng(chrono::steady_clock::now().time_since_epoch().count());
ll exp(ll x,ll y){
  x%=mod;
  ll res=1;
  while(y){
    if(y&1) res=res*x%mod;
    x=x*x%mod, y>>=1;
  }
  return res;
}
ll fact(ll n){
  ll res=1;
  for(ll i=2;i<=n;++i) (res*=i)%=mod;
  return res;
}
bool isprime(ll n){
  if(n<=1) return 0;
  for(ll i=2;i*i<=n;++i) if(n%i==0) return 0;
  return 1;
}
class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& v) {
        using ll=long long;
        int n=sz(v),m=sz(v[0]);
        vector<vector<ar<ll,2>>> mx(n);
        for(int i=0;i<n;++i){
          for(int j=0;j<m;++j) mx[i].push_back({v[i][j],j});
          sort(all(mx[i]));
        }
        ll ans=-1ll<<60;
        for(int i=0;i<n;++i)
        for(int j=i+1;j<n;++j)
        for(int k=j+1;k<n;++k){
          vector<ar<ll,2>> tmp;
          for(int x=m-1;x>=max(0,m-3);--x){
            tmp.push_back(mx[i][x]);
            tmp.push_back(mx[j][x]);
            tmp.push_back(mx[k][x]);
          }
          for(int x=0;x<9;x+=3)
          for(int y=1;y<9;y+=3) if(tmp[y][1]!=tmp[x][1])
          for(int z=2;z<9;z+=3) if(tmp[z][1]!=tmp[y][1]&&tmp[z][1]!=tmp[x][1]){
            ans=max(ans,tmp[x][0]+tmp[y][0]+tmp[z][0]);
             break;
          }
        }
        return ans;
    }
};
