impl Solution {
    pub fn maximum_value_sum(br: Vec<Vec<i32>>) -> i64 {
        let inf = 3_000_000_001i64;
        let mut res = -inf;
        let n = br.len();
        let m = br[0].len();

        for i in 0..n {
            for j in i + 1..n {
                for k in j + 1..n {
                    let mut a = vec![-inf; 8];
                    a[0] = 0;
                    for q in 0..m {
                        for st in (0..=6).rev() {
                            if (st & (1 << 0)) == 0 {
                                let st2 = st | (1 << 0);
                                a[st2] = a[st2].max(a[st] + br[i][q] as i64);
                            }
                            if (st & (1 << 1)) == 0 {
                                let st2 = st | (1 << 1);
                                a[st2] = a[st2].max(a[st] + br[j][q] as i64);
                            }
                            if (st & (1 << 2)) == 0 {
                                let st2 = st | (1 << 2);
                                a[st2] = a[st2].max(a[st] + br[k][q] as i64);
                            }
                        }
                    }
                    res = res.max(a[7]);
                }
            }
        }
        res
    }
}
