#define ll long long
const int N = 100, M = 100;
pair<int, int> maxi[N][M];
class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& arr) {
        ll n = arr.size(), m = arr[0].size();

        for(int i = 0; i < n; i++){
            for(int j = 0; j < m; j++)
                maxi[i][j] = {arr[i][j],j};
            sort(maxi[i], maxi[i]+m, greater<pair<int,int>>());
        }

        vector<array<ll, 3>> v;
        ll ans = -1e10;
        for(int i = 0; i < n; i++){
            for(int j = 0; j < m; j++){
                v.clear();
                for(int k = 0; k < n; k++){
                    if(i == k) continue;
                    v.push_back({maxi[k][0].first, k, maxi[k][0].second});
                    v.push_back({maxi[k][1].first, k, maxi[k][1].second});
                    v.push_back({maxi[k][2].first, k, maxi[k][2].second});
                }
                sort(v.begin(), v.end(), greater<array<ll, 3>>());

                for(int x = 0; x < 6; x++){
                    for(int y = x+1; y < 6; y++){
                        auto &[val1, I1, J1] = v[x];
                        auto &[val2, I2, J2] = v[y];

                        if(i != I1 && i != I2 && I1 != I2 &&
                           j != J1 && j != J2 && J1 != J2)
                            ans = max(ans, val1+val2+arr[i][j]);
                    }
                }
            }
        }
        return ans;
    }
};
