using ll = long long;
class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        long long ans=-1e10;
        ll n=board.size(),m=board[0].size();
        vector<vector<pair<ll,ll>>> top3(n);
        for(int i=0;i<n;i++){
            priority_queue<pair<ll,ll>> pq;
            for(int j=0;j<m;j++) pq.push({board[i][j],j});
            for(int j=0;j<3;j++){
                top3[i].push_back(pq.top()); pq.pop();
            }
        }
        for(int i=0;i<n;i++){
            for(int j=i+1;j<n;j++){
                for(int k=j+1;k<n;k++){
                    for(int i1=0;i1<3;i1++){
                        for(int j1=0;j1<3;j1++){
                            if(top3[i][i1].second==top3[j][j1].second) continue;
                            for(int k1=0;k1<3;k1++){
                                if(top3[i][i1].second!=top3[k][k1].second && (top3[k][k1].second!=top3[j][j1].second)) ans=max(ans,top3[i][i1].first+top3[j][j1].first+top3[k][k1].first);
                            }
                        }
                    }
                }
            }
        }
        return ans;
    }
};
