using ll = long long;


class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        const int rows = board.size();
        const int cols = board[0].size();

        vector<vector<pair<ll, int>>> vec(rows, vector<pair<ll, int>>(6, {LLONG_MIN / 4, 0}));

        ll mn, mni;

        for (int row = 0; row < rows; ++row) {
            for (int col = 0; col < cols; ++col) {
                mn = LLONG_MAX;
                for (int i = 0; i < 6; ++i) {
                    if (vec[row][i].first < mn) {
                        mn = vec[row][i].first;
                        mni = i;
                    }
                }
                if (board[row][col] > mn) {
                    vec[row][mni] = {board[row][col], col};
                }
            }
        }

        vector<int> taken(cols + 1);
        ll ans = LLONG_MIN;
        ll sum = 0;

        for (int i = 0; i < rows - 2; ++i) {
            for (int ii = 0; ii < 6; ++ii) {
                taken[vec[i][ii].second] = true;
                sum = vec[i][ii].first;
                for (int j = i + 1; j < rows - 1; ++j) {
                    for (int jj = 0; jj < 6; ++jj) {
                        if (taken[vec[j][jj].second])
                            continue;
                        taken[vec[j][jj].second] = true;
                        sum += vec[j][jj].first;
                        for (int k = j + 1; k < rows; ++k) {
                            for (int kk = 0; kk < 6; ++kk) {
                                if (taken[vec[k][kk].second])
                                    continue;
                                ans = max(ans, sum + vec[k][kk].first);
                            }
                        }
                        sum -= vec[j][jj].first;
                        taken[vec[j][jj].second] = false;
                    }
                }
                taken[vec[i][ii].second] = false;
                sum -= vec[i][ii].first;
            }

        }

        return ans;

    }
};
