class Solution {
public:
    #define ll long long
    long long maximumValueSum(vector<vector<int>>& board) {
        ll n = board.size(), m = board[0].size(), ans = LLONG_MIN;
        vector<vector<pair<ll,ll>>> rs(n, vector<pair<ll,ll>> (3, make_pair(LLONG_MIN, LLONG_MIN)));
        for(ll i = 0; i < n; i++)
        {
            for(ll j = 0; j < m; j++)
            {
                if(board[i][j] > rs[i][0].first)
                {
                    rs[i][0] = {board[i][j], j};    
                }
                if(rs[i][0].first > rs[i][1].first)
                {
                    swap(rs[i][0], rs[i][1]);
                }
                if(rs[i][1].first > rs[i][2].first)
                {
                    swap(rs[i][1], rs[i][2]);
                }
                // cs[j].insert({board[i][j], i});
            }
        }
        for(ll i = 0; i < n; i++)
        {
            unordered_set<ll> cl;
            // it = rs[i][2];
            // it--;
            for(ll j = 0; j < 3; j++)
            {
                pair<ll,ll> it = rs[i][2-j];
                cl.insert((it).second);
                for(ll k = i+1; k < n; k++)
                {
                    if(k != i)
                    {
                        // xt--;
                        for(ll l = 0; l < 3; l++)
                        {
                            pair<ll,ll> xt = rs[k][2-l];
                            if(cl.find((xt).second) == cl.end())
                            {
                                cl.insert((xt).second);
                                for(ll p = k+1; p < n; p++)
                                {
                                    if(p != i && p != k)
                                    {
                                        // yt--;
                                        for(ll q = 0; q < 3; q++)
                                        {
                                            pair<ll,ll> yt = rs[p][2-q];
                                            if(cl.find((yt).second) == cl.end())
                                            {
                                                ll cur = (it).first + (xt).first + (yt).first;
                                                ans = max(ans, cur);
                                            }
                                            // yt = rs[p][2-q-1];
                                        }
                                    }
                                }
                                cl.erase((xt).second);
                            }
                            // xt = rs[k][2-l-1];
                        }
                    }
                }
                cl.erase((it).second);
            }
        }
        return ans;
    }
};
