class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& vec) {
        int n=vec.size();
        int m=vec[0].size();
        vector<vector<pair<long long int,int>>>temp(n);
        for(int i=0;i<n;i++)
        {
            for(int j=0;j<m;j++)
            {
                temp[i].push_back({vec[i][j],j});
            }
            sort(temp[i].begin(),temp[i].end());
            reverse(temp[i].begin(),temp[i].end());
        }
        long long int ans=-1e18;
        for(int i=0;i<n;i++)
        {
            for(int j=i+1;j<n;j++)
            {
                for(int k=j+1;k<n;k++)
                {
                    long long int sum=-1e18;
                    for(int p=0;p<3;p++)
                    {
                        for(int q=0;q<3;q++)
                        {
                            for(int r=0;r<3;r++)
                            {
                                 if(temp[i][p].second!=temp[j][q].second && temp[j][q].second!=temp[k][r].second &&temp[k][r].second!=temp[i][p].second)
                                sum=max(sum,1ll*(temp[i][p].first+temp[j][q].first+temp[k][r].first));
                            }
                        }
                    }
                    ans=max(ans,sum);
                }
            }
        }
        return ans;
    }
};
