class Solution {
public:
long long int solve(int vis1,int vis2, vector<vector<pair<int,int>>>&arr,int i,int n){
    long long int ans=-10000000000000;
    for(int in=i;in<=n;in++){
        for(int ink=0;ink<3;ink++){
            if(vis1!=arr[in][ink].second && vis2!=arr[in][ink].second){
                ans=max(ans,1LL*arr[in][ink].first);
                break;
            }
        }
    }
    return ans;
}
long long int solve2( vector<vector<pair<int,int>>>&arr,int i,int j, vector<vector<long long int>>&dp){
    if(j<=i){
        return -10000000000000;
    }
    if(dp[i][j]!=-1){
        return dp[i][j];
    }
   long long int ans=-10000000000000;
   for(int in=0;in<3;in++){
                for(int ij=0;ij<3;ij++){
                    if(1LL*arr[i][in].second!=arr[j][ij].second){
                  ans=max(ans,1LL*arr[i][in].first+1LL*arr[j][ij].first+solve(arr[i][in].second,arr[j][ij].second,arr,i+1,j-1));
                    }
                }
            }

            return dp[i][j]=max(ans,max(solve2(arr,i+1,j,dp),solve2(arr,i,j-1,dp)));

}
    long long maximumValueSum(vector<vector<int>>& board) {
        int n=board.size();
        int m=board[0].size();
        vector<vector<pair<int,int>>>arr(n);
        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){
           arr[i].push_back({board[i][j],j});
            }
        }
        for(int i=0;i<n;i++){
            sort(arr[i].begin(),arr[i].end());
            reverse(arr[i].begin(),arr[i].end());
        }
        int i=0;
        int j=n-1;
        vector<vector<long long int>>dp(n,vector<long long int>(n,-1));
        return solve2(arr,i,j,dp);
    }
};
