class Solution:
    def maximumValueSum(self, board: List[List[int]]) -> int:
        board = [sorted([(x, i) for i, x in enumerate(row)], reverse=True)[: 3] for row in board]
        n = len(board)
        ans = -inf
        for a in combinations(range(n), 3):
            for x, i in board[a[0]]:
                for y, j in board[a[1]]:
                    if i == j: continue
                    for z, k in board[a[2]]:
                        if j == k or i == k:
                            continue
                        ans = max(ans, x + y + z)
        return ans
