class Solution {
public:
    bool ok(vector<int>&a ,vector<int>&b, vector<int>&c){
        if(a[1]==b[1] || a[1]==c[1] || b[1]==c[1]){
            return false;
        }

        if(a[2]==b[2] || a[2]==c[2] || b[2]==c[2]){
            return false;
        }
        return true;
    }
    long long maximumValueSum(vector<vector<int>>& board) {
        long long ans=-1e18;
        int n=board.size(),m=board[0].size();
        vector<vector<int>> v;
        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){
                v.push_back({board[i][j],i,j});
            }
        }
        sort(v.begin(),v.end());
        reverse(v.begin(),v.end());
        int x=v.size();
        for(int i=0;i<min(101,x);i++){
            for(int j=i+1;j<min(i+102,x);j++){
                for(int k=j+1;k<min(j+102,x);k++){

                    long long a=v[i][0],b=v[j][0],c=v[k][0];
                    if(ok(v[i],v[j],v[k])){
                        ans = max(ans, a+b+c);
                    }
                }
            }
        }
        return ans;
    }
};
