class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        vector<vector<long long>> points;
        int R = board.size();
        int C = board[0].size();

        for (int i = 0;i < R; i++) {
            for (int j = 0; j < C; j++) {
                points.push_back({board[i][j] * 1LL, i * 1LL, j * 1LL});
            }
        }

        sort(points.begin(), points.end(), [](const std::vector<long long>& f, const std::vector<long long>& s) {
            return f[0] > s[0];
        });

        long long maxVal = LONG_MIN;
        long long a = LONG_MIN, b = LONG_MIN, c = LONG_MIN;
        for (int i = 0 ;i < points.size(); i++) {
            for (int j = i + 1; j < points.size(); j++) {
                if (points[j][1] == points[i][1] || points[j][2] == points[i][2]) continue;

                if (b != LONG_MIN && a != LONG_MIN && a + b > points[i][0] + points[j][0] && points[j][0] < c) break;

                for (int k = j + 1; k < points.size(); k++) {
                    if (points[k][1] == points[j][1] || points[k][1] == points[i][1] || points[k][2] == points[j][2] || points[k][2] == points[i][2]) continue;
                    if (points[i][0] + points[j][0] + points[k][0] > maxVal) {
                        maxVal = points[i][0] + points[j][0] + points[k][0];
                        a = points[i][0];
                        b = points[j][0];
                        c = points[k][0];
                    }
                    break;
                }

            }
        }


        return maxVal;
    }
};
