const int N = (int)500 + 7;
const int inf = (int)1e9 + 7;
const int mod = (int)1e9 + 7;
const ll linf = (ll)1e18 + 7;

const int dx[] = {-1, 0, 1, 0, 1, -1, -1, 1};
const int dy[] = {0, 1, 0, -1, 1, -1, 1, -1};

using namespace std;


class Solution {
public:
    vector<pair<int,int>> st[N];

    ll dp[501][501];
    long long maximumValueSum(vector<vector<int>>& a) {
        int n = sz(a);
        int m = sz(a[0]);
        rep(i, 0, n - 1) {
            st[i].resize(m);
            rep(j, 0, m - 1) {
                st[i][j] = {a[i][j], j};
            }
            sort(all(st[i]));
            reverse(all(st[i]));
            while (sz(st[i]) > 3) st[i].pp();
        }

        ll ans = -linf;
        rep(i, 0, m-  1) rep(j, 0, m - 1) dp[i][j] = -inf;
        per(i, n - 1, 0) {
            rep(j, 0, i - 1) {
                for (auto it : st[i]) {
                    for (auto jt : st[j]) {
                        if (it.s == jt.s) continue;
                        ans = max(ans, dp[min(it.s, jt.s)][max(it.s, jt.s)] + it.f + jt.f);
                    }
                }
            }
            rep(x, 0, m - 1) rep(y, x + 1, m - 1) {
            for (auto it : st[i]) {
                    if (it.s == x || it.s == y) continue;
                    dp[x][y] = max(dp[x][y], (ll)it.f);
                }
            }
        }
        return ans;
    }
};
