class Solution:
    def maximumValueSum(self, a: List[List[int]]) -> int:

        r, c, ans = len(a), len(a[0]), []

        for i in range(r):
            for j in range(c):
                ans.append((i, j, a[i][j]))

        ans.sort(key = lambda tt: (-tt[2], tt[0], tt[1]))

        ans2, tt = [], len(ans)

        for i in range(tt):
            x, y, z = ans[i]
            ct = 0
            for j in range(i + 1, tt):
                x1, y1, z1 = ans[j]
                if x1 != x and y1 != y:
                    ans2.append((x, y, z + z1, x1, y1))
                    if ct + 1 >= 4:
                        break
                    ct = ct + 1

        ans3 = []
        ttt = len(ans2)
        ans2.sort(key = lambda tt: -tt[0])
        for i in range(ttt):
            x, y, z, x1, y1 = ans2[i]
            for j in range(tt):
                x2, y2, z2 = ans[j]
                if x2 != x and x2 != x1 and y2 != y and y2 != y1:
                    ans3.append(z + z2)
                    break    

        return max(ans3)
