class Solution(object):
    def maximumValueSum(self, board):
        """
        :type board: List[List[int]]
        :rtype: int
        """
        m=len(board)
        n=len(board[0])
        if m>=n:
            b=[[board[i][j] for i in range(m)] for j in range(n)]
        else:
            b=board
        m=len(b)
        n=len(b[0])
        a=[[[-float('inf'),-1] for i in range(3)] for j in range(m)]

        for i in range(m):
            for j in range(n):
                if b[i][j]>=a[i][0][0]:
                    a[i][2][:]=a[i][1][:]
                    a[i][1][:]=a[i][0][:]
                    a[i][0]=[b[i][j],j]
                elif b[i][j]>=a[i][1][0]:
                    a[i][2][:]=a[i][1][:]
                    a[i][1]=[b[i][j],j]
                elif b[i][j]>=a[i][2][0]:
                    a[i][2]=[b[i][j],j]
        dp0=[0]*m
        dp1=[[-float('inf')]*n for i in range(m)]
        dp2=[[[-float('inf')]*n for i in range(n)] for j in range(m)]
        dp1[0][:]=b[0][:]
        for i in range(1,m):
            for j in range(n):
                dp1[i][j]=max(dp1[i-1][j],b[i][j])
        for j in range(n):
            for k in range(j+1,n):
                dp2[1][j][k]=max(b[0][j]+b[1][k],b[0][k]+b[1][j])
        for i in range(2,m):
            for j in range(n):
                for k in range(j+1,n):
                    dp2[i][j][k]=max(dp2[i-1][j][k],dp1[i-1][j]+b[i][k],dp1[i-1][k]+b[i][j])
        r=-float('inf')
        for i in range(2,m):
            for j in range(n):
                for k in range(j+1,n):
                    if a[i][0][1]!=j and a[i][0][1]!=k:
                        r=max(r,dp2[i-1][j][k]+a[i][0][0])
                    elif a[i][1][1]!=j and a[i][1][1]!=k:
                        r=max(r,dp2[i-1][j][k]+a[i][1][0])
                    elif a[i][2][1]!=j and a[i][2][1]!=k:
                        r=max(r,dp2[i-1][j][k]+a[i][2][0])
        return r
