class Solution {
public:
    int LIM = 7;
    bool ok(vector<int> a, vector<int> b, vector<int> c){
        if(a.size()!=3||b.size()!=3|c.size()!=3)return false;
        if(a[2]==b[2]||a[2]==c[2]||b[2]==c[2]||a[1]==b[1]||a[1]==c[1]||b[1]==c[1])return false;
        return true;
    }
    long long maximumValueSum(vector<vector<int>>& mat) {
        int n=mat.size(), m=mat[0].size();
        vector<vector<vector<int>>> a(min(m, LIM));
        for(int i=0;i<n;i++){
            vector<vector<int>> b(m);
            for(int j=0;j<m;j++){
                b[j] = {mat[i][j], j, i};
            }
            sort(b.begin(), b.end());
            reverse(b.begin(), b.end());
            for(int j=0;j<min(m,LIM);j++){
                a[j].push_back(b[j]);
            }
        }

//         for(int r1 = 0; r1 < n; r1++){
//             for(int r2 = r1+1; r2 < n; r2++){
//                 vector<int> p(3);
//                 p[0]=0, p[1]=1,p[2]=2;
//                 do{

//                 }while(next_permutation(p.begin(), p.end()))
//             }
//         }


        for(int j=0;j<min(m, LIM);j++){
            sort(a[j].begin(), a[j].end());
            reverse(a[j].begin(), a[j].end());
        }

        vector<vector<int>> v;
        for(int x=0;x<min(m,LIM);x++){
            for(int y=0;y<min(n,LIM);y++){
                v.push_back(a[x][y]);
            }
        }

        long long ans = -1e15;

        for(int i=0;i<v.size();i++){
            for(int j=i+1;j<v.size();j++){
                for(int k=j+1;k<v.size();k++){
                    if(ok(v[i], v[j], v[k])){
                        ans=max(ans, 1ll*v[i][0]+1ll*v[j][0]+1ll*v[k][0]);
                    }
                }
            }
        }

//         cout<<v.size()<<"\n";
//         for(int i=0;i<v.size();i++){
//             for(int j=0;j<v[i].size();j++){
//                 cout<<v[i][j]<<" ";
//             }cout<<"\n";
//         }
// //         
        return ans;


    }
};
