#define ll long long
class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        ll n = board.size();
        ll m = board[0].size();
        vector<tuple<ll, ll, ll>> cells;
        for (ll i = 0; i < n; i++) {
            for (ll j = 0; j < m; j++) {
                cells.push_back({board[i][j], i, j});
            }
        }
        // N*M
        sort(cells.begin(), cells.end());
        reverse(cells.begin(), cells.end());
        ll sz = cells.size();

        ll maxSum = -1e18;

        for (ll i = 0; i < min(100ll, sz); i++) {
            auto [v1, r1, c1] = cells[i];
            for (ll j = i + 1; j < min(100ll, sz); j++) {
                auto [v2, r2, c2] = cells[j];
                if (r1 == r2 || c1 == c2) continue;

                for (ll k = j + 1; k < min(100ll, sz); k++) {
                    auto [v3, r3, c3] = cells[k];
                    if (r1 == r3 || r2 == r3 || c1 == c3 || c2 == c3) continue; 

                    ll currentSum = v1 + v2 + v3;
                    maxSum = max(maxSum, currentSum);
                }
            }
        }


        return maxSum;
    }
};
