 * @return {number}
 */
var maximumValueSum = function(board) {
    const m = board.length;
    const n = board[0].length;

    const mat = [];
    for (let i = 0; i < m; i += 1) {
        let max1 = [[-Infinity, -1], [-Infinity, -1], [-Infinity, -1]];
        for (let j = 0; j < n; j += 1) {
            if (board[i][j] >= max1[2][0]) {
                max1 = [max1[1], max1[2], [board[i][j], j]];
            } else if (board[i][j] >= max1[1][0]) {
                max1 = [max1[1], [board[i][j], j], max1[2]];
            } else if (board[i][j] >= max1[0][0]) {
                max1 = [[board[i][j], j], max1[1], max1[2]];
            }
        }
        mat.push(max1);
    }   
    //console.log(mat);

    let res2 = Array(n).fill(-Infinity);
    let res3 = -Infinity;
    let max2 = [[-Infinity, -1], [-Infinity, -1], [-Infinity, -1]];
    const getMax2 = (p, row) => {
        let rrr = -Infinity;
        for (let w = 0; w < 3; w += 1) {
            for (let z = 0; z < 3; z += 1) {
                if ((mat[row + 1][w][1] !== p) && (mat[row + 2][z][1] !== p) && (mat[row + 1][w][1] !== mat[row + 2][z][1])) {
                    rrr = Math.max(rrr, (mat[row + 1][w][0] + mat[row + 2][z][0]));
                }
            }
        }
        return rrr;
    };
    for (let i = (m - 3); i >= 0; i -= 1) {
        for (let j = 0; j < n; j += 1) {
            res2[j] = Math.max(res2[j], (getMax2(j, i)));
            res3 = Math.max(res3, (board[i][j] + res2[j]));
        }
        for (let g = 0; g < 3; g += 1) {
            const arr = [...mat[i + 1], ...mat[i + 2]].sort((a, b) => a[0] - b[0]);
            console.log('arrrrrr', arr)
            let iii = 4;
            const mySet = new Set([arr.at(-1)[1]]);
            const fff = [[...arr[arr.length - 1]]];
            while (iii >= 0) {
                if (!mySet.has(arr[iii][1])) {
                    fff.push([...arr[iii]]);
                    mySet.add(arr[iii][1]);
                }
                iii -= 1;
            }
            fff.length = 3;
            fff.reverse();
            mat[i + 1] = fff;
            console.log('arr', fff)
        }
        //console.log('mat', mat);
    }
    return res3;
};
