impl Solution {
    pub fn maximum_value_sum(board: Vec<Vec<i32>>) -> i64 {
        let H = board.len();
        let W = board[0].len();
        let mut rows = vec![vec![(0, 0); W]; H];
        for h in 0..H {
            for w in 0..W {
                rows[h][w] = (board[h][w], w);
            }
            rows[h].sort_by_key(|x| -x.0);
        }
        let mut ans = i64::MIN;
        for h1 in 0..H {
            for h2 in h1+1..H {
                for h3 in h2+1..H {
                    for d1 in 0..3 {
                        for d2 in 0..3 {
                            if rows[h1][d1].1 == rows[h2][d2].1 {
                                continue;
                            }
                            for d3 in 0..3 {
                                if rows[h1][d1].1 == rows[h3][d3].1 {
                                    continue;
                                }
                                if rows[h2][d2].1 == rows[h3][d3].1 {
                                    continue;
                                }
                                ans = ans.max(
                                    rows[h1][d1].0 as i64 + rows[h2][d2].0 as i64 + rows[h3][d3].0 as i64
                                );
                            }
                        }
                    }

                }
            }
        }
        ans
    }
}
