class Solution:
    def maximumValueSum(self, board: List[List[int]]) -> int:
        ordered = []
        for i in range(len(board)):
            for j in range(len(board[0])):
                ordered.append(tuple([board[i][j], i, j]))
        ordered.sort(reverse = True)
        max_seen = float("-inf")
        for first in ordered:
            if max_seen > first[0] * 3:
                break
            for second in ordered:
                candidate = first[0] + second[0]
                if second[0] > first[0]:
                    continue
                if max_seen - first[0] > second[0] * 2:
                    continue
                if second[1] == first[1] \
                    or second[2] == first[2]:
                    continue
                for third in ordered:
                    if third[0] > second[0]:
                        continue
                    if third[1] == first[1] \
                        or third[1] == second[1] \
                        or third[2] == first[2] \
                        or third[2] == second[2]:
                        continue
                    max_seen = max(max_seen, candidate + third[0])
        return max_seen
