class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& b) {
        int n = b.size(), m = b[0].size();
        std::vector<std::vector<std::pair<int, int>>> bb(n);

        for (int i = 0; i < n; ++i) {
            bb[i].reserve(m);
            for (int j = 0; j < m; ++j) {
                bb[i].emplace_back(b[i][j], j);
            }
            std::sort(bb[i].rbegin(), bb[i].rend()); 
            if (bb[i].size() > 3) {
                bb[i].resize(3); 
            }
        }

        long long ans = (long long)-3*1000000000;

        for (int i = 0; i < n; ++i) {
            for (int j = i + 1; j < n; ++j) {
                for (int k = j + 1; k < n; ++k) {
                    for (int ii = 0; ii < 3; ++ii) {
                        for (int jj = 0; jj < 3; ++jj) {
                            if (bb[i][ii].second == bb[j][jj].second) continue;
                            for (int kk = 0; kk < 3; ++kk) {
                                if (bb[i][ii].second == bb[k][kk].second || bb[j][jj].second == bb[k][kk].second) continue;
                                long long sum = (long long)bb[i][ii].first + bb[j][jj].first + bb[k][kk].first;
                                ans = max(ans, sum);

                            }
                        }
                    }
                }
            }
        }

        return ans;
    }
};
