class Solution {
public:
    long long maximumValueSum(const vector<vector<int>>& board) {
        const int n = board.size(), m = board.front().size();
        vector<vector<int>> row_max(n, vector<int>(3, -1));
        for (int i = 0; i < n; ++i) {
            for (int j = 0; j < m; ++j) {
                if (row_max[i][0] == -1 || board[i][j] > board[i][row_max[i][0]]) {
                    row_max[i][2] = row_max[i][1];
                    row_max[i][1] = row_max[i][0];
                    row_max[i][0] = j;
                } else if (row_max[i][1] == -1 || board[i][j] > board[i][row_max[i][1]]) {
                    row_max[i][2] = row_max[i][1];
                    row_max[i][1] = j;
                } else if (row_max[i][2] == -1 || board[i][j] > board[i][row_max[i][2]]) {
                    row_max[i][2] = j;
                }
            }
        }

        long long result = -1e18;
        for (int i = 0; i < n - 2; ++i) {
            for (int j = i + 1; j < n - 1; ++j) {
                for (int k = j + 1; k < n; ++k) {
                    for (int a1 = 0; a1 < 3; ++a1) {
                        for (int a2 = 0; a2 < 3; ++a2) {
                            for (int a3 = 0; a3 < 3; ++a3) {
                                if (row_max[i][a1] == row_max[j][a2]) {
                                    continue;
                                }
                                if (row_max[i][a1] == row_max[k][a3]) {
                                    continue;
                                }
                                if (row_max[j][a2] == row_max[k][a3]) {
                                    continue;
                                }
                                long long cur = board[i][row_max[i][a1]];
                                cur += board[j][row_max[j][a2]];
                                cur += board[k][row_max[k][a3]];
                                result = max(result, cur);
                            }
                        }
                    }
                }
            }
        }
        return result;
    }
};
