class Solution {
public:
    pair<int,int> bst[505][3];

    long long maximumValueSum(vector<vector<int>>& board) {
        typedef pair<int,int> PII;
        int n = board.size(), m = board[0].size();
        for(int i=0;i<n;i++){
            priority_queue<PII> q;
            for(int j=0;j<m;j++){
                q.push({board[i][j], j});
            }

            for(int j=0;j<3;j++){
                bst[i][j] = q.top();
                q.pop();
            }
        }    

        long long ans = -1e18;
        for(int i=0;i<n;i++){
            for(int j=i+1;j<n;j++){
                for(int k=j+1;k<n;k++){
                    for(int a=0;a<3;a++){
                        for(int b=0;b<3;b++){
                            for(int c=0;c<3;c++){
                                auto p1 = bst[i][a], p2 = bst[j][b], p3 = bst[k][c];
                                if(p1.second == p2.second || p1.second == p3.second || p2.second == p3.second) continue;
                                ans = max(ans, (long long)p1.first + p2.first + p3.first);
                            }
                        }
                    }
                }
            }
        }

        return ans;
    }
};
