class Solution:
    def maximumValueSum(self, board: List[List[int]]) -> int:
        n = len(board)
        m = len(board[0])
        row = [[] for _ in range(n)]
        for i in range(n):
            for j in range(m):
                row[i].append(j)
                row[i].sort(key=lambda x: board[i][x], reverse=True)
                if len(row[i]) > 3:
                    row[i] = row[i][:3]
        ans = -math.inf
        for i in range(n):
            for j in range(i + 1, n):
                for k in range(j + 1, n):
                    x = i
                    y = j
                    z = k
                    for a in row[x]:
                        for b in row[y]:
                            for c in row[z]:
                                if a != b and a != c and b != c:
                                    ans = max(ans, board[x][a] + board[y][b] + board[z][c])
        return ans
