class Solution:
    def maximumValueSum(self, board: List[List[int]]) -> int:
        n, m = map(len, (board, board[0]))
        h = []
        for i in range(n):
            for j in range(m):
                heappush(h, (- board[i][j], i, j))
        top = []
        for _ in range(min(20, m * n)):
            top.append(heappop(h)[1:])
        ret = - inf
        for comb in itertools.combinations(top, 3):
            if all(comb[i1][j] != comb[i2][j] for i1 in range(3) for i2 in range(i1 + 1, 3) for j in range(2)):
                ret = max(ret, sum(board[i][j] for i, j in comb))
        return ret
