class Solution {
    fun maximumValueSum(board: Array<IntArray>): Long {
        var ans = -4_000_000_000L
        val (m, n) = board.size to board[0].size

        val mx = MutableList(m) { MutableList(3) { -4_000_000_000L to -1 } }

        for (r in 0..<m) {
            for (c in 0..<n) {
                if (board[r][c] > mx[r][0].first) {
                    mx[r][2] = mx[r][1]
                    mx[r][1] = mx[r][0]
                    mx[r][0] = board[r][c].toLong() to c
                } else if (board[r][c] > mx[r][1].first) {
                    mx[r][2] = mx[r][1]
                    mx[r][1] = board[r][c].toLong() to c
                } else if (board[r][c] > mx[r][2].first) {
                    mx[r][2] = board[r][c].toLong() to c
                }
            }
        }

        for (r1 in 0..<m) {
            for (r2 in 0..<m) {
                if (r1 == r2) continue
                for (r3 in 0..<m) {
                    if (r3 == r1 || r3 == r2) continue

                    for (c1 in 0..2) {
                        for (c2 in 0..2) {
                            if (mx[r1][c1].second == mx[r2][c2].second) continue
                            for (c3 in 0..2) {
                                if (mx[r3][c3].second == mx[r1][c1].second || mx[r3][c3].second == mx[r2][c2].second) continue
                                ans = max(ans, mx[r1][c1].first + mx[r2][c2].first + mx[r3][c3].first)
                            }
                        }
                    }

                }
            }
        }

        return ans
    }
}
