class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        using ll = long long;
        ll m = board[0].size();
        ll n = board.size();
        vector<vector<pair<ll, ll>>> maxes(m, vector<pair<ll, ll>>(3, {INT_MIN, -1}));
        for(int i = 0; i < m; i++) {
            for(int q = 0; q < n; q++) {
                ll ind = q;
                ll c = board[q][i];
                for(int j = 0; j < 3; j++) {
                    if (c > maxes[i][j].first) {
                        swap(maxes[i][j].first, c);
                        swap(maxes[i][j].second, ind);
                    }
                }
            }
            // cout << i << endl;
            // cout << maxes[i][0].first << ' ' << maxes[i][0].second << endl;
            // cout << maxes[i][1].first << ' ' << maxes[i][1].second << endl;
            // cout << maxes[i][2].first << ' ' << maxes[i][2].second << endl;
        }
        ll ma = -1e9 * 4;
        // cout << ma << endl;
        for(int i = 0; i < m; i++) {
            for(int q = 0; q < m; q++) {
                for(int j = 0; j < m; j++) {
                    if (i == q || i == j || j == q) continue;
                    for(int a = 0; a < 3; a++) {
                        for(int b = 0; b < 3; b++) {
                            for(int c = 0; c < 3; c++) {
                                pair<ll, ll> aa = maxes[i][a];
                                pair<ll, ll> bb = maxes[j][b];
                                pair<ll, ll> cc = maxes[q][c];
                                if (aa.second == bb.second) continue;
                                if (bb.second == cc.second) continue;
                                if (aa.second == cc.second) continue;
                                ma = max(aa.first + bb.first + cc.first, ma);
                            }
                        }
                    }
                }
            }
        }
        return ma;
    }
};
