class Solution:
    def maximumValueSum(self, board: List[List[int]]) -> int:
        rows = []
        n = len(board)
        m = len(board[0])
        for i in range(n):
            upd = []
            for j in range(m):
                upd.append([board[i][j], j])
                upd.sort(reverse=True)
                if len(upd) == 4:
                    upd.pop()
            rows.append(upd)
        # print(rows)
        res = -3 * (10**9)
        for i in range(n):
            for j in range(i+1, n):
                for k in range(j+1, n):
                    for v, idx in rows[i]:
                        for v1, idx1 in rows[j]:
                            for v2, idx2 in rows[k]:
                                if idx1 != idx and idx1 != idx2 and idx != idx2:
                                    res = max(res, v+v1+v2)
        return res
