class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        int n=board.size(),m=board[0].size();
        vector<int>mx(n+1,-1e9-10),sx(n+1,-1e9-10),tx(n+1,-1e9-10),mloc(n+1),sloc(n+1),tloc(n+1);
        vector<vector<pair<int,int>>>vec(n+1,vector<pair<int,int>>(3));
        for(int i=0;i<n;i++) {
            for(int k=0;k<m;k++) {
                if(board[i][k]>mx[i]) {
                    tx[i]=sx[i],sx[i]=mx[i];
                    tloc[i]=sloc[i],sloc[i]=mloc[i];
                    mx[i]=board[i][k];
                    mloc[i]=k;
                } else if(board[i][k]>sx[i]) {
                    tx[i]=sx[i];
                    tloc[i]=sloc[i];
                    sx[i]=board[i][k];
                    sloc[i]=k;
                } else if(board[i][k]>tx[i]) {
                    tloc[i]=k;
                    tx[i]=board[i][k];
                }
            }
            vec[i][0]={mx[i],mloc[i]};
            vec[i][1]={sx[i],sloc[i]};
            vec[i][2]={tx[i],tloc[i]};
        }
        long long res=-1e10;
        for(int i=0;i<n;i++) {
            for(int k=i+1;k<n;k++) {
                for(int j=k+1;j<n;j++) {
                    for(int x=0;x<3;x++) {
                        for(int y=0;y<3;y++) {
                            for(int z=0;z<3;z++) {
                                if(vec[i][x].second==vec[j][z].second or vec[i][x].second==vec[k][y].second or vec[k][y].second==vec[j][z].second) continue;
                                res=max(res,(long long)vec[i][x].first+vec[k][y].first+vec[j][z].first);
                            }
                        }
                    }
                }
            }
        }
        return res;
    }
};
