class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& M) {
        vec<set<v2i>> mas(M[0].size());
        auto merge = [](set<v2i>& t, ll val, ll index)
        {
            t.insert({val, index});
            if(t.size() > 10) t.erase(t.begin());
        };

        rrep(i, j, M.size(), M[0].size()) merge(mas[j], M[i][j], i);
        vec<v3i> cc;
        rep(j, M[0].size())
        {
            each(it, mas[j])
            {
                cc.push_back({it.x, it.y, j});
            }
        }

        ranges::sort(cc, greater<>());
        ll mx = -inf64;
        rep(i1, min((ll)cc.size(), (ll)100))
        {
            rep(i2, i1 + 1, min((ll)cc.size(), (ll)100))
            {
                rep(i3, i2 + 1, min((ll)cc.size(), (ll)100))
                {
                    if(cc[i3].y == cc[i2].y || cc[i3].z == cc[i2].z ||
                        cc[i3].y == cc[i1].y || cc[i3].z == cc[i1].z ||
                        cc[i2].y == cc[i1].y || cc[i2].z == cc[i1].z) continue;

                    chmax(mx, cc[i3].x + cc[i2].x + cc[i1].x);
                }
            }
        }

        return mx;
    }
};
