class Solution {
    public long maximumValueSum(int[][] board) {
        int n=board.length;
        int m=board[0].length;
        long [][] ans=new long [n][3];
        long [][] ind=new long [n][3];
        int i=0,j=0,k=0;
        long m1=0,m2=0,m3=0;
        for(i=0;i<n;i++)
        {
            m1=Long.MIN_VALUE;
            m2=Long.MIN_VALUE;
            m3=Long.MIN_VALUE;
            for(j=0;j<m;j++)
            {
                  if(board[i][j]>=m1)
                  {
                    m3=m2;
                    m2=m1;
                    m1=board[i][j];
                    ind[i][2]=ind[i][1];
                    ind[i][1]=ind[i][0];
                    ind[i][0]=j;
                  }
                  else if(board[i][j]>=m2)
                  {
                    m3=m2;
                    m2=board[i][j];
                    ind[i][2]=ind[i][1];
                    ind[i][1]=j;
                  }
                  else if(board[i][j]>m3)
                  {
                       m3=board[i][j];
                       ind[i][2]=j;
                  }
            }
            ans[i][0]=m1;
            ans[i][1]=m2;
            ans[i][2]=m3;
        }
        long answer=Long.MIN_VALUE;
        int l1=0,l2=0,l3=0;
        for(i=0;i<n;i++)
        {
            for(j=i+1;j<n;j++)
            {
                for(k=j+1;k<n;k++)
                {
                      for(l1=0;l1<3;l1++)
                      {
                        for(l2=0;l2<3;l2++)
                        {
                            for(l3=0;l3<3;l3++)
                            {
                                if(ind[i][l1]!=ind[j][l2] && ind[j][l2]!=ind[k][l3] && ind[k][l3]!=ind[i][l1])
                                answer=Math.max(answer,ans[i][l1]+ans[j][l2]+ans[k][l3]);
                            }
                        }
                      }
                }
            }
        }
        return answer;
    }
}
