class Solution {
public:
    typedef long long ll;
    typedef pair<int, int> ii;
    long long maximumValueSum(vector<vector<int>>& board) {
        int m = board.size(), n = board[0].size();
        vector<vector<ii>> a(m, vector<ii> (3));
        vector<ii> v(n);
        for (int i = 0; i < m; i++) {
            for (int j = 0; j < n; j++) {
                v[j] = {board[i][j], j};
            }
            sort(v.begin(), v.end(), greater<ii>());
            for (int j = 0; j < 3; j++) {
                a[i][j] = v[j];
            }
        }
        ll ans = -1e15;
        for (int i = 0; i < m; i++) {
            for (int j = 0; j < m; j++) {
                if (i == j) continue;
                for (int k = 0; k < m; k++) {
                    if (k == i || k == j) continue;
                    for (int l1 = 0; l1 < 3; l1++) {
                        for (int l2 = 0; l2 < 3; l2++) {
                            for (int l3 = 0; l3 < 3; l3++) {
                                ii x1 = a[i][l1], x2 = a[j][l2], x3 = a[k][l3];
                                if (x1.second == x2.second || x1.second == x3.second || x2.second == x3.second) continue;
                                ans = max(ans, 0ll + x1.first + x2.first + x3.first);
                            }
                        }
                    }
                }
            }
        }
        return ans;
    }
};
