class Solution:
    def maximumValueSum(self, board: List[List[int]]) -> int:
        R, C = len(board), len(board[0])
        top_cs = [list(nlargest(3, range(C), key=lambda c: board[r][c])) for r in range(R)]
        res = -inf
        for r1 in range(R):
            for r2 in range(r1 + 1, R):
                for r3 in range(r2 + 1, R):
                    for c1 in top_cs[r1]:
                        for c2 in top_cs[r2]:
                            if c1 == c2:
                                continue
                            for c3 in top_cs[r3]:
                                if c1 == c3 or c2 == c3:
                                    continue
                                res = max(res, board[r1][c1] + board[r2][c2] + board[r3][c3])
        return res
