class Solution {
    public long maximumValueSum(int[][] board) {
        int[][] maxthree = new int[board.length][3];
        for (int i = 0; i < board.length; i++) {
            maxthree[i] = findmaxthree(board[i]);
        }
        long res = Long.MIN_VALUE;
        for (int i = 0; i < maxthree.length; i++) {
            for (int j = 0; j < 3; j++) {
                // a的取值[i, j]
                for (int k = i + 1; k < maxthree.length; k++) {
                    for (int l = 0; l < 3; l++) {
                        if (maxthree[i][j] == maxthree[k][l]) {
                            continue;
                        }
                        // b的取值[k, l]
                        for (int m = k + 1; m < maxthree.length; m++) {
                            for (int n = 0; n < 3; n++) {
                                // [m,n]
                                if (maxthree[i][j] == maxthree[m][n]) {
                                    continue;
                                }
                                if (maxthree[k][l] == maxthree[m][n]) {
                                    continue;
                                }
                                long t = board[i][maxthree[i][j]];
                                t += board[k][maxthree[k][l]];
                                t += board[m][maxthree[m][n]];
                                res = Math.max(t, res);
                            }
                        }
                    }
                }
            }
        }
        return res;
    }

    private int[] findmaxthree(int[] ints) {
        int[] res = new int[3];
        res[0] = 0;
        res[1] = 1;
        res[2] = 2;
        if (ints[res[2]] > ints[res[1]]) {
            swap(res, 1, 2);
        }
        if (ints[res[1]] > ints[res[0]]) {
            swap(res, 1, 0);
        }
        if (ints[res[2]] > ints[res[1]]) {
            swap(res, 1, 2);
        }
        if (ints[res[1]] > ints[res[0]]) {
            swap(res, 1, 0);
        }
        for (int i = 3; i < ints.length; i++) {
            if (ints[i] > ints[res[2]]) {
                res[2] = i;
                if (ints[res[2]] > ints[res[1]]) {
                    swap(res, 1, 2);
                    if (ints[res[1]] > ints[res[0]]) {
                        swap(res, 0, 1);
                    }
                }
            }

        }
        return res;
    }

    private void swap(int[] res, int a, int b) {
        int t = res[a];
        res[a] = res[b];
        res[b] = t;
    }
}
