class Solution {
    typedef priority_queue<
            vector<int>, 
            vector<vector<int>>,
            greater<vector<int>>
        > pvq;
    typedef vector<vector<int>> vv;

    vector<vector<int>> getMax(vector<vector<int>>& board, int k, int i1=-1, int j1=-1, int i2=-1, int j2=-1) {
        pvq pq;
        int n = board.size(), m = board[0].size();

        for(int i=0; i<n; i++) {
            for(int j=0; j<m; j++) {
                if(i==i1 || i==i2 ||j==j1 || j==j2) continue;

                pq.push({board[i][j], i, j});
                if(pq.size() > k) {
                    pq.pop();
                }
            }
        }       

        vector<vector<int>> res = {};
        while(!pq.empty()) {
            res.push_back(pq.top());
            pq.pop();
        }
        return res;
    }

public:
    long long maximumValueSum(vector<vector<int>>& board) {
        int n = board.size(), m = board[0].size();
        vv max3 = getMax(board, 4);

        long long ans = -1e12;
        for(vector<int>& num: max3) {
            long long val = num[0], i1 = num[1], j1 = num[2];

            vv max32 = getMax(board, 3, i1, j1);
            for(vector<int>& num2: max32) {
                long long val2 = num2[0], i2 = num2[1], j2 = num2[2];

                vv pq3 = getMax(board, 1, i1, j1, i2, j2);

                ans = max(ans, val + val2 + pq3[0][0]);
            }
        }

        return ans;
    }

};
