 * @return {number}
 */
var maximumValueSum = function(board) {
    let n = board.length
    let m = board[0].length
    let result = board[0][0] + board[1][1] + board[2][2]

    for (let i = 0; i < n; i++) {
        for (let j = i + 1; j < n; j++)
            for (let k = j + 1; k < n; k++) {
                let maxCol = new Array(3)
                let maxInCol = new Array(3)
                maxInCol[0] = Math.max(board[i][0], board[i][1])
                maxInCol[1] = Math.max(board[j][0], board[j][1])
                maxInCol[2] = Math.max(board[k][0], board[k][1])
                maxCol[0] = Math.max(board[i][0] + board[j][1], board[i][1] + board[j][0])
                maxCol[1] = Math.max(board[i][0] + board[k][1], board[i][1] + board[k][0])
                maxCol[2] = Math.max(board[j][0] + board[k][1], board[j][1] + board[k][0])
                for (let col = 2; col < m; col++) {
                    let maxVal = maxCol[0] + board[k][col]
                    result = Math.max(result, maxCol[0] + board[k][col], maxCol[1] + board[j][col], maxCol[2] + board[i][col])
                    maxCol[0] = Math.max(maxCol[0], board[i][col] + maxInCol[1], board[j][col] + maxInCol[0])
                    maxCol[1] = Math.max(maxCol[1], board[i][col] + maxInCol[2], board[k][col] + maxInCol[0])
                    maxCol[2] = Math.max(maxCol[2], board[j][col] + maxInCol[2], board[k][col] + maxInCol[1])
                    maxInCol[0] = Math.max(maxInCol[0], board[i][col])
                    maxInCol[1] = Math.max(maxInCol[1], board[j][col])
                    maxInCol[2] = Math.max(maxInCol[2], board[k][col])
                }
            }
    }

    return result
};
