#define ll long long int
ll dp[101][4][101][101];
class Solution {
public:
    ll find(vector<vector<int>>& board,ll m,ll n,ll ind,ll count,ll first,ll second){
        if(count==3){
            return 0;
        }
        if(ind==n){
            return -1e10;
        }
        if(dp[ind][count][first][second]!=-1e15){
            return dp[ind][count][first][second];
        }
        ll one=find(board,m,n,ind+1,count,first,second);
        ll two=-1e10;
        for(ll i=0;i<m;i++){
            if(i==first||i==second){
                continue;
            }
            if(count==0){
                two=max(two,board[i][ind]+find(board,m,n,ind+1,count+1,i,second));
            }else if(count==1){
                two=max(two,board[i][ind]+find(board,m,n,ind+1,count+1,first,i));
            }else if(count==2){
                two=max(two,board[i][ind]+find(board,m,n,ind+1,count+1,first,second));
            }
        }
        return dp[ind][count][first][second]=max(one,two);
    }
    long long maximumValueSum(vector<vector<int>>& board) {
        ll m=board.size();//row
        ll n=board[0].size();//col
        //ind,count
        for(ll i=0;i<=n;i++){
            for(ll j=0;j<=3;j++){
                for(ll k=0;k<=m;k++){
                    for(ll a=0;a<=m;a++){
                        dp[i][j][k][a]=-1e15;
                    }
                }
            }
        }
        ll ans=find(board,m,n,0,0,m,m);
        return ans;
    }
};
